    /**
     * This method checks if the object name or description contains one of 
     * the obscene words in the table
     * @param service
     * @return 
     */
    public static String checkObsceneWords(Service service) {
        
        String description = service.getDescription().toLowerCase();
        String nameObject = service.getNameObject().toLowerCase();
        boolean findWordObscene = false;
        int i = 0;
        while (!findWordObscene || i!=tabWordsObscenes.length)
        {
                findWordObscene = nameObject.contains(tabWordsObscenes[i].toLowerCase()) || description.contains(tabWordsObscenes[i].toLowerCase());
                i++;
        }
        
        return i==tabWordsObscenes.length ? "" : tabWordsObscenes[i];
    }
